mapper.select:
SELECT * FROM ( SELECT ROWNUM as rn, a.* FROM (
SELECT EMPLOYEE_ID , FIRST_NAME , LAST_NAME , EMAIL , PHONE_NUMBER "phone" , HIRE_DATE , JOB_ID , SALARY , COMMISSION_PCT , MANAGER_ID , DEPARTMENT_ID FROM employees e
ORDER BY first_name
)a WHERE rownum <= #{svo.end} ) b WHERE rn >= #{svo.start}

mapper.insert:

		select employees_seq.nextval from dual

	INSERT INTO employees
