	   , EMAIL
	   , HIRE_DATE
	   , JOB_ID
	 ,SALARY
	   , MANAGER_ID
	   , DEPARTMENT_ID)
	VALUES(
	  #{employeeId}
	  , #{firstName}
	  , #{lastName}
	  , #{email}
	  , #{hireDate}
	  , #{jobId}
	 , #{salary}
	  , #{managerId}
      , #{departmentId}
	)
